"use client";

import { useEffect, useRef, useState } from "react";
import { gsap } from "gsap";
import { basePath } from "@/helpers/general";
import { useCopy } from "@/contexts/CopyContext";
import { useScrollTrigger } from "@/hooks/useScrollTrigger";
import { ANIMATION_CONFIG } from "@/helpers/scroll";

const STEPS = 5; // 1 intro + 4 stats

export default function JourneySection() {
  const sectionRef = useRef<HTMLDivElement>(null);
  const text1Ref = useRef<HTMLDivElement>(null);
  const journeyPathRef = useRef<HTMLDivElement>(null);
  const statRefs = [
    useRef<HTMLDivElement>(null),
    useRef<HTMLDivElement>(null),
    useRef<HTMLDivElement>(null),
    useRef<HTMLDivElement>(null),
  ];

  const [showMethodTooltip, setShowMethodTooltip] = useState<boolean>(false);
  const [currentStep, setCurrentStep] = useState<number>(0);
  const previousStepRef = useRef(0);

  // Set initial visibility
  useEffect(() => {
    const text1 = text1Ref.current;
    const journeyPath = journeyPathRef.current;
    const stats = statRefs.map((ref) => ref.current);

    if (!text1 || !journeyPath || stats.some((s) => !s)) return;

    // Get all children of each stat container
    const allStatChildren = stats.map((stat) =>
      stat!.querySelectorAll(".stat-content")
    );
    const flatStatChildren = allStatChildren.flat();

    // Initial state - all stats and journey path hidden, intro text visible
    gsap.set([journeyPath, ...flatStatChildren], { autoAlpha: 0, y: 30 });
    gsap.set(text1, { autoAlpha: 1, y: 0 });
  }, []);

  // Handle step transitions
  useEffect(() => {
    const text1 = text1Ref.current;
    const journeyPath = journeyPathRef.current;
    const stats = statRefs.map((ref) => ref.current);

    if (!text1 || !journeyPath || stats.some((s) => !s)) return;

    const previousStep = previousStepRef.current;

    // Kill all ongoing animations first
    gsap.killTweensOf([text1, journeyPath]);

    // Handle journey path and visibility based on current step
    if (currentStep === 0) {
      // On intro screen

      // Hide journey path
      gsap.to(journeyPath, {
        autoAlpha: 0,
        y: 30,
        duration: ANIMATION_CONFIG.duration,
        ease: ANIMATION_CONFIG.fadeOut.ease,
      });

      // Hide all stats immediately
      stats.forEach((stat) => {
        if (stat) {
          const statContent = stat.querySelectorAll(".stat-content");
          gsap.killTweensOf(statContent);
          gsap.set(statContent, { autoAlpha: 0 });
        }
      });

      // Show intro text
      gsap.killTweensOf(text1);
      gsap.fromTo(
        text1,
        { autoAlpha: 0, y: 30 },
        {
          autoAlpha: 1,
          duration: ANIMATION_CONFIG.duration,
          ...ANIMATION_CONFIG.fadeIn,
        }
      );
    } else {
      // On a stat screen (steps 1-4)

      // Fade out intro text if coming from intro
      if (previousStep === 0) {
        gsap.to(text1, {
          autoAlpha: 0,
          duration: ANIMATION_CONFIG.duration,
          ...ANIMATION_CONFIG.fadeOut,
        });
      } else {
        // Already on stats - hide intro immediately
        gsap.set(text1, { autoAlpha: 0 });
      }

      // Ensure journey path is visible
      if (previousStep === 0) {
        // Coming from intro - fade in
        gsap.fromTo(
          journeyPath,
          { autoAlpha: 0, y: 30 },
          {
            autoAlpha: 1,
            ...ANIMATION_CONFIG.fadeIn,
          }
        );
      } else {
        // Already on stats - ensure it's visible immediately
        gsap.set(journeyPath, { autoAlpha: 1, y: 0 });
      }

      // Hide all stats, then show only the current one
      stats.forEach((stat, index) => {
        if (stat) {
          const statContent = stat.querySelectorAll(".stat-content");
          gsap.killTweensOf(statContent);

          const statIndex = index + 1; // Stats are 1-indexed

          if (statIndex === currentStep) {
            // Current stat - fade it in
            gsap.fromTo(
              statContent,
              { autoAlpha: 0, y: 30 },
              {
                autoAlpha: 1,
                duration: ANIMATION_CONFIG.duration,
                ...ANIMATION_CONFIG.fadeIn,
              }
            );
          } else {
            // Other stats - hide immediately
            gsap.set(statContent, { autoAlpha: 0 });
          }
        }
      });
    }

    previousStepRef.current = currentStep;
  }, [currentStep]);

  // Set up ScrollTrigger
  useScrollTrigger({
    sectionRef,
    steps: STEPS,
    onStepChange: setCurrentStep,
  });

  const tooltipText = useCopy("context_button_method_tooltip");

  return (
    <div
      ref={sectionRef}
      className="relative w-full h-screen"
      data-section="journey"
    >
      <div className="relative w-full h-full flex items-center justify-center">
        {/* Intro screen */}
        <div
          ref={text1Ref}
          className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 w-full opacity-0 invisible"
        >
          <div className="max-w-[1728px] px-10 m-auto mb-16">
            <div className="text-[32px] max-w-[900px]">
              {useCopy("context_intro")}
            </div>
          </div>
          <img
            src={`${basePath}/icons/contextPath.svg`}
            alt="Context Path"
            className="w-full max-w-[80%] h-auto ml-[-5px]"
          />
          <div className="max-w-[1728px] flex justify-between items-end gap-2 px-10 m-auto mt-16">
            <div className="text-[32px] max-w-[900px]">
              {useCopy("context_buttons_text")}
            </div>
            <button
              className="relative button-grey-text-hover bg-black-blue border-grey-text border rounded text-grey-text flex items-center justify-center gap-2 hover:bg-grey-text hover:text-black-blue transition"
              onMouseEnter={() => setShowMethodTooltip(true)}
              onMouseLeave={() => setShowMethodTooltip(false)}
            >
              <span className="">{useCopy("context_button_method")}</span>
              <svg
                width="30"
                height="18"
                viewBox="0 0 30 18"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M1.25 10.25C6.65 -1.75 22.85 -1.75 28.25 10.25"
                  strokeWidth="2.5"
                  className="stroke-grey-text no-fill"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                />
                <path
                  d="M14.749 16.2501C14.1581 16.2501 13.5729 16.1337 13.0269 15.9076C12.481 15.6814 11.9849 15.35 11.567 14.9321C11.1492 14.5142 10.8177 14.0182 10.5916 13.4722C10.3654 12.9262 10.249 12.3411 10.249 11.7501C10.249 11.1592 10.3654 10.574 10.5916 10.028C10.8177 9.48208 11.1492 8.986 11.567 8.56814C11.9849 8.15028 12.481 7.81881 13.0269 7.59266C13.5729 7.36652 14.1581 7.25012 14.749 7.25012C15.9425 7.25012 17.0871 7.72423 17.931 8.56814C18.7749 9.41205 19.249 10.5566 19.249 11.7501C19.249 12.9436 18.7749 14.0882 17.931 14.9321C17.0871 15.776 15.9425 16.2501 14.749 16.2501Z"
                  className="stroke-grey-text fill-grey-text with-fill"
                  strokeWidth="2.5"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                />
              </svg>
              <div>
                {showMethodTooltip && (
                  <div className="absolute bottom-[54px] right-0  text-black-blue bg-grey-text text-[18px] w-[380px] p-4 rounded-[20px] pointer-events-none normal-case text-left leading-[100%]">
                    {tooltipText}
                  </div>
                )}
              </div>
            </button>
          </div>
        </div>

        {/* Stats screens with grid */}
        <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 w-full max-w-[1728px] px-10 pointer-events-none">
          <div className="grid grid-cols-[1fr_1.5fr_1fr] grid-rows-[1fr_1.5fr] gap-15">
            <div
              ref={journeyPathRef}
              style={{ gridArea: "1 / 1 / 2 / 4" }}
              className="opacity-0 invisible"
            >
              <JourneyPath step={currentStep} />
            </div>

            {[1, 2, 3, 4].map((step) => (
              <StatDisplay
                key={step}
                step={step}
                passRef={statRefs[step - 1]}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

function StatDisplay({
  step,
  passRef,
}: {
  step: number;
  passRef: React.RefObject<HTMLDivElement | null>;
}) {
  return (
    <div ref={passRef} style={{ display: "contents" }}>
      <div
        className="stat-content flex flex-col justify-end opacity-0 invisible"
        style={{
          gridArea: "2 / 2 / 3 / 3",
        }}
      >
        <div className="text-grey-text text-[40px] text-center font-bold mb-4">
          {useCopy(`journey_${step}_stat_headline`)}
        </div>
        <div className="text-bright-green text-[260px] text-center font-semibold leading-none">
          {useCopy(`journey_${step}_stat_number`)}
        </div>
        <div className="text-bright-green text-[24px] text-center text-balance">
          {useCopy(`journey_${step}_stat_text`)}
        </div>
      </div>
      <div
        className="stat-content flex flex-col justify-end opacity-0 invisible"
        style={{
          gridArea: "2 / 3 / 3 / 4",
        }}
      >
        <div className="text-grey-text text-[14px] text-balance">
          {useCopy(`journey_${step}_stat_source`)}
        </div>
      </div>
    </div>
  );
}

